import React, { useState, useEffect } from 'react';
import {
  Alert,
  AppBar,
  Toolbar,
  IconButton,
  Snackbar,
  Typography
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import WalletsList from './WalletsList';
import DepositVault from './DepositVault';
import WithdrawalVault from './WithdrawalVault';
import { useInterval } from './useInterval';
var axios = require('axios');

// var vaultId = 'da5b8e573e389a1f953652c9200a4374' //Long hold vault
var vaultId = '7600f629964efcffdae0f4bcc5cc8e8c' //Vault #4
// var walletId = 'd03e5a8a474988dfba09267fef9ff1de'; //Long hold vault
var walletId = 'b02758652b94b06bfcae7f40d9a2b759'; //Vault #4
var walletQuery = `https://api.anchorage-development.com/v2/vaults/${vaultId}/wallets`;
var transactionQuery = `https://api.anchorage-development.com/v2/transactions?walletId=${walletId}&types=DEPOSIT`
var apiKey = 'bcee5a6d3753e69a838f7518143b61f07e47999f1dabc235d1776e8fcb414d72';

export default function App() {
  // const [isLoading, setLoading] = useState(true);
  const [wallets, getWallets] = useState('');
  const [title, setTitle] = useState('');
  const [open, setOpen] = React.useState(false);
  const [amount, setAmount] = React.useState('');
  const [assetType, setType] = React.useState('');
  const [initialLength, setLength] = React.useState();
  const delay = 1000;

  // useEffect(() => {
  //   getAllWallets();
  // }, []);
  //
  // const getAllWallets = () => {
  //   axios.get(walletQuery, {
  //         headers: {
  //           'Api-Access-Key': apiKey
  //         }
  //       })
  //   .then((res) => {
  //     const allWallets = res.data.data;
  //     getWallets(allWallets);
  //     setTitle(allWallets[0].vaultName);
  //   })
  //   .catch(err => console.log(`Error: ${err}`));
  // }

  // const getAllTransactions = () => {
  //   axios.get(transactionQuery, {
  //         headers: {
  //           'Api-Access-Key': apiKey
  //         }
  //       })
  //   .then((res) => {
  //     console.log(res.data.data);
  //   })
  //   .catch(err => console.log(`Error: ${err}`))
  // };

  useInterval(() => {
    axios.get(transactionQuery, {
          headers: {
            'Api-Access-Key': apiKey
          }
        })
    .then((res) => {
      console.log(res.data.data);
      if(initialLength !== res.data.data.length){
        console.log(initialLength)
        console.log(res.data.data.length)
        setLength(res.data.data.length);
        setAmount(res.data.data[0].amount.quantity);
        setType(res.data.data[0].assetType);
        setOpen(true);
        console.log('happening');
        // alert(`Deposit of ${res.data.data[0].amount.quantity} ${res.data.data[0].assetType} received (Status: ${res.data.data[0].status})`);
      }
    })
    .catch(err => console.log(`Error: ${err}`))
  }, delay);

  // const handleClose = () => {
  //   setOpen(false);
  //   getAllWallets();
  // };

  return (
    <div>
    <AppBar position="static">
      <Toolbar variant="dense">
        <IconButton edge="start" color="inherit" aria-label="menu" sx={{ mr: 2 }}>
          <MenuIcon />
        </IconButton>
        <Typography variant="h6" color="inherit" component="div">
          Crypto Exchange
        </Typography>
      </Toolbar>
    </AppBar>
    <DepositVault wallets={wallets}/>
    </div>
  )
};
